import React, { useRef, useState } from "react";
import { Keyboard, Pagination, Navigation } from "swiper";
import { Swiper, SwiperSlide }from "swiper/react";
import 'swiper/swiper.min.css';
import 'swiper/modules/pagination/pagination.min.css';
import Image from "../elements/Image";

export default function HorizontalScroll() {
  return (
       <Swiper
          slidesPerView={3}
          spaceBetween={20}
          // centeredSlides={true}
          keyboard={{
            enabled: true,
          }}
          // pagination={{
          //   clickable: true,
          // }}
          pagination={{
            type: "progressbar",
          }}
          navigation={true}
          modules={[Keyboard, Pagination, Navigation]}
          className="mySwiper"
       >
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
         <SwiperSlide >
           <Image
              src={require('./../../assets/images/paysage.png')}
              alt="Features split 01"
              width={150}
              height={150} />
         </SwiperSlide>
       </Swiper>
  );
}
